#define ll long long
#define mod 1000000007
class Solution {
public:
    ll dp[55][2005];
    int n;
    ll ans(ll last,ll i,vector<int>&nums)
    {
        if(i==n+1)
            return 1;
        if(dp[last][i]!=-1)
            return dp[last][i];
        ll final=0;
        if(i==1)
        {
            for(int j=0;j<=nums[i];j++)
            {
                final+=ans(j,i+1,nums);
                final%=mod;
            }
        }
        else
        {
             ll x=last;
            ll y=nums[i-1]-last;
            for(int j=0;j<=y;j++)
            {
                if(x+y-j<=nums[i]&&nums[i]-y+j>=x)
                {
                    final+=ans(nums[i]-y+j,i+1,nums);
                    final%=mod;
                }

            }

        }
        return dp[last][i]=final;   

    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        vector<int>ee;
        ee.push_back(0);
        for(auto x:nums)
            ee.push_back(x);
        return ans(0,1,ee);   
    }
};
