class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        @cache
        def fun(ind,lasti,lastj):
            if ind==n:
                return 1
            ans = 0
            for i in range(51):
                if i>=lasti:
                    if nums[ind]>=i:
                        second = nums[ind] - i
                        if second<=lastj:
                            ans+=fun(ind+1,i,second)
            return ans
        return fun(0,-1,inf) % (10**9+7)
