class Solution {
public:
    int mod = 1e9 + 7;

    int countOfPairs(vector<int>& v) {
        int n = v.size();
        vector<int> a, b;
        vector<vector<vector<int>>> dp(
            n, vector<vector<int>>(51, vector<int>(51, -1)));
        return sol(0, v, 0, 50, dp) % mod;
    }
    int sol(int id, vector<int>& v, int a, int b,
            vector<vector<vector<int>>>& dp) {
        if (id == v.size()) {
            return 1;
        }
        long long ans = 0;
        if (dp[id][a][b] != -1)
            return dp[id][a][b];
        for (int i = a; i <= v[id]; ++i) {
            int j = v[id] - i;
            if (j < 0)
                continue;
            if (i >= a && j <= b) {
                // a.push_back(i);
                // b.push_back(j);
                ans = (ans + sol(id + 1, v, i, j, dp)) % mod;
                // a.pop_back();
                // b.pop_back();
            }
        }
        return dp[id][a][b] = ans;
    }
};
