class Solution {
public:
    int mod=1e9+7;
    long long monopair(vector<int>&arr,vector<vector<vector<long long>>>&dp,int fixed,int st1,int st2)
    {
        if(arr.size()<=st1) return 1ll;
        long long pairs=0;
        if(dp[st1][st2][fixed]!=-1) return dp[st1][st2][fixed];
        for(int i=st2;i<=arr[st1];i++)
        {
            for(int j=fixed;j>=0;j--)
            {
                if(arr[st1]!=(i+j)) continue;
                pairs=((pairs%mod)+(monopair(arr,dp,j,st1+1,i)%mod))%mod;
            }
        }
        return dp[st1][st2][fixed]=pairs;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<long long>>>dp(n,vector<vector<long long>>(51,vector<long long>(51,-1)));
        int pairs=monopair(nums,dp,nums[0],0,0);
        return pairs;
    }
};
