class Solution {
public:
    int dp[2000][51], mod=1e9+7;
    int util(vector<int> &nums, int i, int next){
        if(i==-1) return 1;
        if(dp[i][next]!=-1) return dp[i][next];
        int ret=0;
        for(int j=nums[i]; j>=0; j--){
            if(i+1!=nums.size() && nums[i]-j<nums[i+1]-next) continue;
            if(j>next) continue;
            ret=(ret%mod+util(nums, i-1, j)%mod)%mod;
        }
        return dp[i][next]=ret;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return util(nums, nums.size()-1, 50);
    }
};
