class Solution
{
public:
    const int mod = 1e9 + 7;
    vector<int> nums;
    int pre[2002][51][51];
    int solve(int idx, int end1, int end2)
    {
        if (idx == nums.size())
            return 1;

        if (pre[idx][end1][end2] != -1)
            return pre[idx][end1][end2];

        int res = 0;
        for (int i = end1; i <= nums[idx]; i++)
            if (nums[idx] - i <= end2)
                res += solve(idx + 1, i, nums[idx] - i) % mod, res %= mod;

        return pre[idx][end1][end2] = res;
    }

    int countOfPairs(vector<int> &arr)
    {
        nums = arr;
        memset(pre, -1, sizeof(pre));
        return solve(0, 0, 50);
    }
};
