class Solution {
public:
    int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(int i, int arr1, int arr2, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;  
        }

        if (dp[i][arr1][arr2] != -1) {
            return dp[i][arr1][arr2];  
        }

        int count = 0;
        for (int a = 0; a <= nums[i]; ++a) {
            int b = nums[i] - a;
            if ((i == 0 || arr1 <= a) && (i == 0 || arr2 >= b)) {
                count += solve(i + 1, a, b, nums);
                count %= mod;  
            }
        }

        return dp[i][arr1][arr2] = count;  
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));  
        return solve(0, 0, 50, nums);  
    }
};
