class Solution {
    public int countOfPairs(int[] nums) {
        int mod = (int)1e9 + 7;
        int n = nums.length;

        int[][] dp = new int[n][51];
        Arrays.fill(dp[0], 1);
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int one = j;
                int two = nums[i] - j;
                for (int k = 0; k <= nums[i - 1] && k <= one; k++) {
                    if (nums[i - 1] - k >= two) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % mod;
                    }
                }
            }
        }

        int result = 0;
        for (int i = 0; i <= nums[n - 1]; i++) result = (result + dp[n - 1][i]) % mod;
        return result;

    }
}
