class Solution {
public:
    int countOfPairs(vector<int>& raw) {
        const int MOD = 1e9 + 7;
        int n = raw.size();

        vector<int> nums(n);
        nums[0] = raw[0];
        for (int i = 1; i < n; ++i) {
            nums[i] = raw[i] - raw[i-1];
        }

        vector<vector<int>> dp(n, vector<int>(51, 0));
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            // vector<int> prefixSum(1001, 0);
            // prefixSum[0] = dp[i-1][0];
            // for (int j = 1; j <= 50; ++j) {
            //     prefixSum[j] = (prefixSum[j-1] + dp[i-1][j]) % MOD;
            // }

            for (int j = 0; j <= raw[i-1]; ++j) {
                for (int k = max(0, nums[i]); k <= raw[i] - j; ++k) {
                    dp[i][j+k] = (dp[i][j+k] + dp[i-1][j]) % MOD;
                }
            }
        }

        int result = 0;
        for (int j = 0; j <= raw[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;      
    }
};
