#include <vector>
#include <map>
#include <utility>

const int MOD = 1e9 + 7;

using namespace std;

class Solution {
public:

    int solve(int i, int inc, int dec, vector<int>& nums, map<pair<int, pair<int, int>>, int>& memo) {
        if (i >= nums.size()) {
            return 1;
        }


        auto key = make_pair(i, make_pair(inc, dec));


        if (memo.find(key) != memo.end()) {
            return memo[key];
        }

        int count = 0;
        for (int j = inc; j <= nums[i]; ++j) {
            int arr1 = j;
            int arr2 = nums[i] - j;

            if (arr1 >= 0 && arr2 >= 0 && arr1 >= inc && arr2 <= dec) {
                count = (count + solve(i + 1, arr1, arr2, nums, memo)) % MOD;
            }
        }

        memo[key] = count;
        return count;
    }

    int countOfPairs(vector<int>& nums) {
        map<pair<int, pair<int, int>>, int> memo;
        return solve(0, 0, nums[0], nums, memo);
    }
};
