class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)

        mx = max(nums)

        dp = [[[-1 for i in range(n+1)] for j in range(mx+1)] for w in range(mx+1)]

        mod = 10**9 + 7

        def helper(ind, prev1, prev2):

            if ind >= n:
                return 1

            if dp[prev2][prev1][ind] != -1:
                return dp[prev2][prev1][ind]

            take = 0

            for i in range(nums[ind]+1):
                if prev1 == -1 or (i >= prev1 and prev2 >= nums[ind] - i):
                    take = (take + helper(ind+1, i, nums[ind] - i))%mod

            dp[prev2][prev1][ind] = take

            return dp[prev2][prev1][ind]

            # print()

            return take

        return helper(0, -1, -1)
