#include <vector>
using namespace std;

class Solution {
public:
    static const int MOD = 1'000'000'007;
    vector<vector<vector<int>>> dp;

    int findPairs(const vector<int>& nums, int idx, int prev1, int prev2) {
        if (idx >= nums.size()) {
            return 1;
        }

        if (dp[idx][prev1][prev2] != -1) {
            return dp[idx][prev1][prev2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[idx]; ++i) {
            int arr1_val = i;
            int arr2_val = nums[idx] - i;
            if (arr1_val >= prev1 && arr2_val <= prev2) {
                ans = (ans + findPairs(nums, idx + 1, arr1_val, arr2_val)) % MOD;
            }
        }

        dp[idx][prev1][prev2] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.assign(n, vector<vector<int>>(51, vector<int>(51, -1)));  // nums[i] <= 50
        return findPairs(nums, 0, 0, 50);
    }
};
