class Solution:

  def __init__(self):
    self.nums = []

  @lru_cache(maxsize=None)
  def dp(self, pos, prev_1, prev_2):
    if pos == len(self.nums):
      return 1

    res = 0
    for v1 in range(prev_1, self.nums[pos] + 1):
      v2 = self.nums[pos] - v1
      if v2 <= prev_2:
        res = (res + self.dp(pos + 1, v1, v2)) % (10 ** 9 + 7)
    return res

  def countOfPairs(self, nums: List[int]) -> int:
    self.nums = nums
    return self.dp(0, 0, nums[0])
