#define ll long long
class Solution {
public:
    long long solve(int index, int val1, int val2, vector<int>& values, vector<vector<vector<ll>>>& memo) {
        if (index >= values.size()) return 1;
        ll result = 0;
        ll mod_val = 1e9+7; 
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result += solve(index + 1, i, j, values, memo);
                    result = result % mod_val;
                }
            }
        }
        return memo[index][val1][val2] = result;
    }

    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<ll>>> memo(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(0, 0, values[0], values, memo);
    }
};
