class Solution {
public:
    const int MOD=1000000007;
    vector<vector<vector<int>>> dp;

    int solve(vector<int>& nums, int id, int p1, int p2) 
    {
        if (id == nums.size()) 
        {
            return 1;
        }

        if (dp[id][p1 + 1][p2] != -1) 
        {
            return dp[id][p1 + 1][p2];
        }

        int ans = 0;

        for (int i = 0; i <= nums[id]; i++) 
        {
            if (i >= p1 && nums[id] - i <= p2) 
            {
                ans = (ans + solve(nums, id + 1, i, nums[id] - i)) % MOD;
            }
        }

        return dp[id][p1 + 1][p2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(), vector<vector<int>>(52, vector<int>(52, -1)));
        return solve(nums, 0, -1, 51);
    }
};
