class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), CAP = 0, MOD = 1000000007;
        for (int x : nums)
            CAP = max(CAP, x);
        vector<int> a(CAP + 1, 0), s(CAP + 1, 0);
        for (int i = 0; i <= nums[0]; i++)
            a[i] = 1;
        s[0] = a[0];
        for (int i = 1; i <= CAP; i++)
            s[i] = (s[i - 1] + a[i]) % MOD;
        for (int i = 1; i < n; i++) {
            a = vector<int>(CAP + 1, 0);
            for (int j = 0; j <= nums[i]; j++) {
                int low = nums[i - 1] - nums[i] + j;
                a[j] = low >= 0 ? s[min(low, j)] : 0;
            }
            s[0] = a[0];
            for (int j = 1; j <= CAP; j++)
                s[j] = (s[j - 1] + a[j]) % MOD;
        }
        return s[CAP];
    }
};
