class Solution {
public:
    const int mod=1e9+7;
    int f(int ind,int left,int right,vector<int>& nums,vector<vector<vector<int>>>&dp){
        if(ind==nums.size()){
            return 1;
        }

        if(dp[ind][left][right]!=-1) return dp[ind][left][right]%mod;
        int ans=0;

        for(int i =left;i<=nums[ind];i++){
            int newLeft=i;
            int newRight=nums[ind]-newLeft;
            if(newRight>right) continue;
            ans=(ans+f(ind+1,newLeft,newRight,nums,dp)%mod)%mod;
        }
        return dp[ind][left][right]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {

        vector<vector<vector<int>>>dp(nums.size(),vector<vector<int>>(51,vector<int>(51,-1)));
        return f(0,0,nums[0],nums,dp);

    }
};
