class Solution {
private:

int fun(int i, vi &v, int ap, int bp, vii &dp,int mod) {
        if (i == v.size()) {
            return 1;
        }
        if (dp[i][ap] != -1) {
            return dp[i][ap]; 
        }
        int ans = 0;
        for (int j = 0; j <= v[i]; j++) {
            if (j >= ap && v[i] - j <= bp) {
                ans = (ans + fun(i + 1, v, j, v[i] - j, dp,mod)) % mod;
            }
        }
        return dp[i][ap] = ans;
    }
