#include <vector>
#include <functional>
using namespace std;

#define ll long long int

class Solution {
private:
    static const int MOD = 1'000'000'007;

public:
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<ll>>> dpCache(size, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> solve = [&](int idx, int prev1, int prev2) -> ll {
            if (idx == size) return 1;
            if (dpCache[idx][prev1][prev2] != -1) return dpCache[idx][prev1][prev2];

            ll result = 0;
            for (int x1 = prev1; x1 <= values[idx]; x1++) {
                int x2 = values[idx] - x1;
                if (x2 >= 0 && x2 <= prev2) {
                    result = (result + solve(idx + 1, x1, x2)) % MOD;
                }
            }

            return dpCache[idx][prev1][prev2] = result;
        };

        return solve(0, 0, values[0]);
    }
};
