#define ll long long
class Solution {
public:
    ll mod=1e9+7;
    ll f(ll ind,ll prev1,ll prev2,vector<int>&nums,vector<vector<vector<ll>>> &dp ){
        if(ind==nums.size()) return 1;
        ll ans=0;
        if(dp[ind][prev1+1][prev2+1]!=-1) return dp[ind][prev1+1][prev2+1];
        if(prev1==-1){
            for(ll i=0;i<=nums[ind];i++){
                ans=(ans+f(ind+1,i,nums[ind]-i,nums,dp)%mod)%mod;
            }
        }
        else{
            for(ll i=prev1;i<=nums[ind];i++){
                if(nums[ind]-i<=prev2)
                    ans=(ans+f(ind+1,i,nums[ind]-i,nums,dp)%mod)%mod;
            }
        }
        return dp[ind][prev1+1][prev2+1]=ans%mod;
    }


    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<ll>>> dp(nums.size()+1,vector<vector<ll>>(52,vector<ll>(52,-1)));
        return f(0,-1,-1,nums,dp);
    }
};
