class Solution {
public:
    int mod = (int)(1000000007);
    int rec(int i, int n, int prevA, int prevB, vector<int>& nums, vector<vector<vector<int>>>& dp){
        //base case.
        if(i == n){
            return 1;
        }

        if(dp[i][prevA][prevB] != -1){ return dp[i][prevA][prevB]; }
        int ans = 0;
        for(int k = 0; k <= nums[i]; k++){
            int a = k;
            int b = nums[i]-k;
            if(a >= prevA && b <= prevB){
                ans = (ans + rec(i+1, n, a, b, nums, dp))%mod;
            }
        }
        return dp[i][prevA][prevB] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));
        return rec(0, n, 0, 51, nums, dp);
    }
};
