impl Solution {
    pub fn count_of_pairs(nums: Vec<i32>) -> i32 {
        const M: i64 = 1_000_000_007;
        let n = nums.len();
        let mut m = vec![vec![vec![-1; 51]; 51]; n];

        fn s(i: usize, a: usize, b: usize, nums: &Vec<i32>, m: &mut Vec<Vec<Vec<i64>>>) -> i64 {
            if i >= nums.len() {
                return 1;
            }
            if m[i][a][b] != -1 {
                return m[i][a][b];
            }
            let mut r = 0;
            for x in a..=nums[i] as usize {
                for y in (0..=b).rev() {
                    if x + y == nums[i] as usize {
                        r += s(i + 1, x, y, nums, m);
                        r %= M;
                    }
                }
            }
            m[i][a][b] = r;
            r
        }

        s(0, 0, nums[0] as usize, &nums, &mut m) as i32
    }
}
