class Solution {
private:
    int mod = 1e9 + 7;
    int f(int idx, int val1, int val2, int n, auto &nums, auto& arr1,
          auto& arr2, auto &dp) {

        if(idx == n) return 1;

        if (dp[idx][val1][val2] != -1) return dp[idx][val1][val2];

        int ways = 0;
        for (int i = 0; i <= nums[idx]; i++) {
            if (val1 <= i && val2 >= nums[idx] - i) {
                arr1[idx] = i;
                arr2[idx] = nums[idx] - i;
                ways = (ways + f(idx + 1, arr1[idx], arr2[idx], n, nums, arr1, arr2, dp)) % mod;
            }
        }
        return dp[idx][val1][val2] = ways;;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> arr1(n), arr2(n);
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));

        return f(0, 0, 51, n, nums, arr1, arr2, dp);
    }
};
