import java.util.*;

class Solution {
     long MOD=(long)1e9+7; ;

    public long solve(int idx, int i, int j, int[] nums, long[][][] dp) {
        if (idx >= nums.length) return 1;
        if (dp[idx][i][j] != -1) return dp[idx][i][j];

        long ans = 0;

        for (int k = i; k <= nums[idx]; k++) {
            for (int l = j; l >= 0; l--) {
                if (k + l == nums[idx]) {
                    ans = (ans + solve(idx + 1, k, l, nums, dp)) % MOD;
                }
            }
        }

        return dp[idx][i][j] = ans;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][][] dp = new long[n][51][51];

        // Initialize the dp array with -1
        for(long x[][]:dp){
            for(long y[]:x)
                Arrays.fill(y,-1);
        }

        return (int) solve(0, 0, nums[0], nums, dp);
    }
}
