class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int md = 1e9 + 7;
        vector<vector<long long>> f(nums.size(), vector<long long>(51));
        for(int i = 0; i <= nums[0]; ++i) f[0][i] = 1;
        for(int i = 1; i < nums.size(); ++i) {
            for(int j = 0; j <= nums[i]; ++j) 
                for(int k = 0; k <= nums[i - 1]; ++k) {
                    if (k <= j && (nums[i - 1] - k >= nums[i] - j)) 
                        f[i][j] = (f[i][j] + f[i - 1][k]) % md;
                }
        }

        long long ret = 0;
        for(int i = 0 ; i <= nums.back(); ++i) ret = (ret + f.back()[i]) % md;
        return ret;
    }
};
