class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int ans = 0, mod = 1000000007;
        int[][] mem = new int[n][51];
        for(int j = 0; j <= nums[n-1]; j++) {
            mem[n-1][j] = 1;
        }
        for(int i = n-2; i >= 0; i--) {
            for(int j = 0; j <= nums[i]; j++) {
                for(int k = 0; k <= nums[i+1]; k++) {
                    if(k >= j && nums[i+1]-k <= nums[i]-j) {
                        mem[i][j] = (mem[i][j] + mem[i+1][k])%mod;
                    }
                }
            }
        }
        for(int j = 0; j <= nums[0]; j++) {
            ans = (ans + mem[0][j])%mod;
        }
        return ans;
    }
}
