class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        modulo = 10 ** 9 + 7
        n = len(nums)

        @functools.cache
        def count(index, prev_a1, prev_a2):
            #print(index, prev_a1, prev_a2)
            if index == -1:
                return 1

            s = 0
            for a1 in range(min(nums[index], prev_a1) + 1):
                a2 = nums[index] - a1
                #print("cand", index, a1, a2)
                if a2 < prev_a2:
                    continue
                s = (s + count(index - 1, a1, a2)) % modulo
            return s

        return count(n - 1, 51, 0)
