class Solution {

    int mod = (int)1e9 + 7;
    public int countOfPairs(int[] nums) {

        int n = nums.length;
        Integer[][][] memo = new Integer[n][51][51];

        return solve(memo,nums,0,0,50);

    }

    public int solve(Integer[][][] memo, int[] nums, int ind, int prev1, int prev2){

        if (ind == nums.length)
            return 1;

        if (memo[ind][prev1][prev2] != null)
            return memo[ind][prev1][prev2];

        int count = 0;

        for (int i = prev1; i <= nums[ind]; i++){

            if (nums[ind]-i <= prev2){
                count = (count + solve(memo,nums,ind+1,i,nums[ind]-i))%mod;
            }

        }

        return memo[ind][prev1][prev2] = count;

    }
}
