class Solution {
public:
    vector<vector<int>> dp;
    const int mod=1e9+7;
    int solve(vector<int>& nums, int i, int inc, int dec)
    {
        if(i==nums.size()) return 1;
        if(dp[i][inc]!=-1)
            return dp[i][inc];
        int ans=0;
        for(int j=max(0, inc); j<=nums[i]; j++)
        {
            if(j>=inc && nums[i]-j<=dec)
            {
                ans+=solve(nums, i+1, j, nums[i]-j);
                ans%=mod;
            }
        }
        return dp[i][inc]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.assign(n, vector<int>(51, -1));
        //int ans=0;        
        /*for(int i=0; i<n; i++)
        {

        }*/
        return solve(nums, 0, 0, 50);
    }
};
