const int MOD = 1e9 + 7;
class Solution {
public:

    int solve(int index, int prevOne, int prevTwo, vector<int>& nums, vector<vector<vector<int>>>& dp) {
        if (index == nums.size()) {
            return 1; // Reached the end, valid configuration
        }

        if (dp[index][prevOne][prevTwo] != -1) {
            return dp[index][prevOne][prevTwo];
        }

        int ans = 0;

        for (int i = prevOne; i <= nums[index]; i++) {
            int numOne = i;
            int numTwo = nums[index] - i;

            if (numTwo <= prevTwo) {
                ans = (ans + solve(index + 1, numOne, numTwo, nums, dp)) % MOD;
            }
        }

        return dp[index][prevOne][prevTwo] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return solve(0, 0, 50, nums, dp); // Start with 0 for prevOne and 50 for prevTwo
    }
};
