class Solution {
    int MOD = 1e9 + 7;
    int recc(int i, int lsta, int lstb, vector<vector<vector<int>>>& dp, vector<int>& arr) {
        if (i == arr.size()) return 1;
        if (dp[i][lsta][lstb] != -1) return dp[i][lsta][lstb];

        int ans = 0;
        for (int k = lsta; k <= arr[i]; k++) {
            int newb = arr[i] - k;
            if (newb <= lstb && newb >= 0) {
                ans = (ans + recc(i + 1, k, newb, dp, arr)) % MOD;
            }
        }

        return dp[i][lsta][lstb] = ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return recc(0, 0, 50, dp, nums);
    }
};
