const int MOD = 1e9 + 7;
class Solution {
    int n;
private:
    int f(int ind, int prev1, int prev2, vector<int>& nums, vector<vector<vector<int>>>& dp){
        if(ind == n) return 1;

        if(dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2];

        int res = 0;

        for(int i = prev1; i <= nums[ind]; i++){
            int val = nums[ind] - i;
            if(val >= 0 && val <= prev2){
                res = (res + f(ind + 1, i, val, nums, dp)) % MOD;
            }
        }

        return dp[ind][prev1][prev2] = res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return f(0, 0, 50, nums, dp);
    }
};
