/* eslint-disable no-var, func-style */
const MOD = 1e9 + 7
const MOD_CUT = ((1 << 20) * (1 << 20)) % MOD
function add(a, b) {
    return (a + b) % MOD
}
function minus(a, b) {
    return add(add(a, -b), MOD)
}
function mul(a, b) {
    let r = (a >> 20) * (b >> 20) * MOD_CUT
        + (a & 0xfff00000) * (b & 0xfffff)
        + (a & 0xfffff) * b
    return r % MOD
}
function pow(a, b) {
    let r = 1
    let base = a
    while (b) {
        if (b & 1) {
            r = mul(r, base)
        }
        b >>= 1 // change to divide if b > 1e9 !!
        base = mul(base, base)
    }
    return r
}
/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    const n = nums.length
    const N = 50
    //
    const dp = Array(n)
        .fill(0)
        .map(() => Array(N + 1).fill(0))
    for (let last = 0; last <= nums[0]; last++) {
        dp[0][last] = 1
    }
    for (let i = 1; i < n; i++) {
        for (let last = 0; last <= nums[i]; last++) {
            let temp = 0
            for (let prev = 0; prev <= nums[i - 1]; prev++) {
                if (last >= prev
                        && nums[i] - last <= nums[i - 1] - prev) {
                    temp = add(temp, dp[i - 1][prev])
                }
            }
            dp[i][last] = temp
        }
    }
    // console.log(dp)
    let ans = 0
    for (let last = 0; last <= N; last++) {
        ans = add(ans, dp[n - 1][last])
    }
    return ans
};
