#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> calculate = [&](int currentIndex, int firstValue, int secondValue) -> ll {
            if (currentIndex >= nums.size()) return 1;
            ll ways = 0;
            ll mod = 1e9+7; 
            if (dp[currentIndex][firstValue][secondValue] != -1) return dp[currentIndex][firstValue][secondValue];
            for (int first = firstValue; first <= nums[currentIndex]; first++) {
                for (int second = secondValue; second >= 0; second--) {
                    if (first + second == nums[currentIndex]) {
                        ways += calculate(currentIndex + 1, first, second);
                        ways = ways % mod;
                    }
                }
            }
            return dp[currentIndex][firstValue][secondValue] = ways;
        };

        return calculate(0, 0, nums[0]);
    }
};
