#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1000000007;
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));


        for (int x = 0; x <= nums[0]; ++x) {
            int y = nums[0] - x;
            if (y >= 0) {
                dp[0][x][y] = 1;
            }
        }


        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                if (y < 0) continue;

                for (int prev_x = 0; prev_x <= x; ++prev_x) { // Ensure arr1[i] is non-decreasing
                    for (int prev_y = nums[i-1] - prev_x; prev_y >= y; --prev_y) { // Ensure arr2[i] is non-increasing
                        if (prev_y >= 0 && prev_y <= 50) {
                            dp[i][x][y] = (dp[i][x][y] + dp[i-1][prev_x][prev_y]) % MOD;
                        } else {
                            break;
                        }
                    }
                }
            }
        }


        int result = 0;
        for (int x = 0; x <= nums[n-1]; ++x) {
            int y = nums[n-1] - x;
            if (y >= 0) {
                result = (result + dp[n-1][x][y]) % MOD;
            }
        }

        return result;
    }
};
