    int solve(int i, int prev1, int prev2, vector<int>&nums, int n, vector<vector<vector<int>>>& dp) {
        if (i == n) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int totalCount = 0;   

        for (int x = prev1; x <= nums[i]; ++x) {
            int y = nums[i] - x;

            if (y <= prev2) {
                totalCount += solve(i + 1, x, y, nums, n, dp);
                totalCount %= MOD;
            }
        }
        return dp[i][prev1][prev2] = totalCount;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        // return solve(0,0,nums[0],nums,n);
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(0, 0, nums[0], nums, n, dp);
    }
