class Solution {
public:
    typedef long long ll;
    ll n, M = 1e9 + 7;
    vector<int> a;
    vector<vector<ll>> dp;
    // pre1 smaller, pre2 bigger (larger)
    ll recu(ll pre1, ll i)
    {
        // cout << pre1 << " " << i << "\n";
        ll pre2 = (i > 0 ? a[i - 1] - pre1 : 50);

        if(i == n)
            return 1;
        if(dp[pre1][i] != -1)
            return dp[pre1][i];

        ll ans = 0;
        for(ll thisA = pre1; thisA <= a[i]; thisA++)
        {
            ll thisB = a[i] - thisA;
            if(pre1 <= thisA && pre2 >= thisB)
                ans += recu(thisA, i + 1),
                ans %= M;
            // else
        }

        return dp[pre1][i] = ans;
    }
    int countOfPairs(vector<int> &_a)
    {
        a = _a, n = a.size();
        dp.resize(50 + 1, vector<ll>(n, -1));
        return recu(0, 0);
    }
};
