#define mod 1000000007
int dp[2005][55];

int solve(int i, int prev, vector<int>& nums)
{
    //cout<<i<<" "<<prev<<" "<<endl;
    if(i==nums.size())
        return 1;

    if(dp[i][prev]!=-1)
        return dp[i][prev];

    int ans=0,j,k,y=(i==0?51:nums[i-1]-prev);
    for(j=prev;j<=nums[i];j++)
    {
        k=nums[i]-j;
        //cout<<i<<" "<<prev<<" "<<y<<" "<<j<<" "<<k<<endl;
        if(k>y)
            continue;
        ans=(ans+solve(i+1,j,nums))%mod;
    }
    return dp[i][prev]=ans;
}


class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int ans=0;
        ans=solve(0,0,nums);
        // for(int i=0;i<=nums[0];i++)
        // {
        //     ans=(ans+solve(0,i,nums))%mod;
        // }
        return ans;
    }
};
