class Solution {
public:
    long long swas(int index, int minVal, int maxVal, vector<int>& nums, vector<vector<vector<long long>>>& dp) {
        if (index >= nums.size()) return 1;
        if (dp[index][minVal][maxVal] != -1) return dp[index][minVal][maxVal];

        long long ans = 0;
        for (int i = minVal; i <= nums[index]; i++) {
            for (int j = maxVal; j >= 0; j--) {
                if (i + j == nums[index]) {
                    ans = (ans + swas(index + 1, i, j, nums, dp)) % 1000000007;
                }
            }
        }
        return dp[index][minVal][maxVal] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, -1)));
        return swas(0, 0, nums[0], nums, dp);
    }
};
