class Solution {
public:
    int mod = 1e9+7;
    int f(int i,int last1,int last2,vector<int>&nums,vector<vector<int>>&dp,int n){
        if(i==n)return 1;
        if(dp[i][last1]!=-1)return dp[i][last1];
        int count = 0;
        for(int j=last1;j<=nums[i];j++){
            int k = nums[i] - j;
            if(k<=last2){
                count = (count + f(i+1,j,k,nums,dp,n))%mod;
            }
        }
        return dp[i][last1] = count;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>>dp(n,vector<int>(51,-1));
        return f(0,0,51,nums,dp,n);
    }
};
