class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = max(nums)
        MOD = 1000000007

        dp = [0] * (m + 1)
        s = [0] * (m + 2)

        for i in range(nums[0] + 1):
            dp[i] = 1
            s[i] = i + 1

        for index in range(1, len(nums)):
            num = nums[index]
            dp[:] = [0] * (m + 1)

            for i in range(num + 1):
                b = nums[index - 1]
                a = max(i, nums[index - 1] - (num - i))
                if a <= b:
                    dp[i] = (s[b] - s[a - 1]) % MOD

            for i in range(m + 1):
                s[i] = (s[i - 1] + dp[i]) % MOD

        return s[-2]
