class Solution {
public:
    long long int m = 1e9+7;
    long long int dp[2001][52][52];
    int func(int i,vector<int> &nums,int a,int b){
        int n = nums.size();
        if(i == n) return 1;
        if(dp[i][a][b]!=-1) return dp[i][a][b];
        long long int ans = 0;
            for(int j = 0;j<=nums[i];j++){
                if(j>=a && nums[i]-j<=b){
                    ans= (ans+func(i+1,nums,j,nums[i]-j))%m;
                }
            }
            return dp[i][a][b]=  ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));
        return func(0,nums,0,51);
    }
};
