class Solution {
public:
    int m=1e9+7;
    #define ll long long
    ll f(int i,int n,vector<int>& nums,int last1,int last2,bool inc1,bool inc2,vector<vector<vector<int>>>& dp)
    {
        if(i==n)
        {
          //  cout<<last1<<" "<<last2<<endl;
            return 1;
        }
        if(dp[i][last1][last2]!=-1)
            return dp[i][last1][last2];
        ll x=0;
        for(int j=0;j<=nums[i];j++)
        {
            if(inc1)
            {
                if(j<last1)
                    continue;
                if(inc2)
                {
                    if(nums[i]-j<last2)
                        continue;
                    x+=f(i+1,n,nums,j,nums[i]-j,1,1,dp);
                }
                else
                {
                    if(nums[i]-j>last2)
                        continue;
                    x=(x%m+f(i+1,n,nums,j,nums[i]-j,1,0,dp)%m)%m;
                }
            }
            else
            {
                if(j>last1)
                    continue;
                if(inc2)
                {
                    if(nums[i]-j<last2)
                        continue;
                    x+=f(i+1,n,nums,j,nums[i]-j,0,1,dp);
                }
                else
                {
                    if(nums[i]-j>last2)
                        continue;
                    x+=f(i+1,n,nums,j,nums[i]-j,0,0,dp);
                }
            }

        }
        return dp[i][last1][last2]=x;
    }
    int countOfPairs(vector<int>& nums) {
        int ans=0;
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        for(int i=0;i<=nums[0];i++)
        {
            int x=0;

            x+=f(1,n,nums,i,nums[0]-i,1,0,dp);
           // x+=f(1,n,nums,i,nums[0]-i,1,1);
            ans=(ans%m+x%m)%m;
        }
        return ans;
    }
};
