class Solution {
public:
    int mod = 1e9 + 7;
    long long dp[2002][52][52];
    int solve(vector<int>& nums, int i, int prev1, int prev2)
    {
        int n = nums.size();

        if(i >= n){
            return 1;
        } 
        if(dp[i][prev1][prev2] != -1)
            return dp[i][prev1][prev2];
        // cout << i << " " << a << " " << b  <<endl;
        int num = nums[i];
        int b = 0;
        int a = num;  
        long long ans = 0;
        while(b <= num)
        {
            // if(i== 0)
            //     // cout << a << " " << b  << endl;
            if(a >= prev1 && b <= prev2)
            {
                ans += solve(nums, i + 1, a, b);
                ans %= mod;
            }
            b++, a--;
        }
        return dp[i][prev1][prev2] = ans%mod;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, 0, nums[0]);
    }
};
