class Solution {
public:
    int mx=0;
    int cnt(vector<int>&nums,int idx,int prevFirst,int prevSecond,vector<vector<vector<int>>>&dp)
    {
        if(idx==nums.size())
        {
            return 1;
        }
        if(dp[idx][prevFirst][prevSecond]!=-1)
        {
            return dp[idx][prevFirst][prevSecond];
        }
        long long int ans=0;
        for(int i=prevFirst;i<=mx;i++)
        {
            if(nums[idx]-i<=prevSecond && nums[idx]-i>=0)
            ans=(ans+cnt(nums,idx+1,i,nums[idx]-i,dp))%1000000007;
        }
        return dp[idx][prevFirst][prevSecond]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        mx=0;
        for(int i:nums)
        {
            mx=max(mx,i);
        }
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(mx+1,vector<int>(mx+1,-1)));
        return cnt(nums,0,0,nums[0],dp);
    }
};
