class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dp(start, pre1, pre2):
            # Given pre1, pre2, starting from start, how many?
            if start == len(nums):
                return 1
            # if pre1 > nums[start] or pre2 > nums[start]:
            #     return 0    

            res = 0
            for i1 in range(nums[start], pre1-1, -1):
                i2 = nums[start] - i1
                # print(i1, i2, pre1, pre2)
                if pre2 != -1 and i2 > pre2:
                    break
                res += dp(start+1, i1, i2)
            return res%(10**9 + 7)

        return dp(0, 0, -1)
