class Solution {
public:
long long mod=1e9+7;
    long long findpairs(int ind,int prev1,int prev2,vector<int> &nums,
    vector<vector<vector<long long>>> &dp){
        if(ind==nums.size()) return 1;
        long long cnt=0;
        if(dp[ind][prev1][prev2]!=-1) return dp[ind][prev1][prev2];
        for(int i=0;i<=nums[ind];i++){
            int a=i,b=nums[ind]-i;
            if((prev1==51 || a>=prev1) && (prev2==51 ||  b<=prev2)){
                cnt=(cnt+findpairs(ind+1,a,b,nums,dp))%mod;
            }
        }
        return dp[ind][prev1][prev2] = cnt%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int k=51;
        int n=nums.size();
        vector<vector<vector<long long>>> dp(n+1,vector<vector<long
        long>>(52,vector<long long>(52,-1)));
        return findpairs(0,k,k,nums,dp);
    }
};
