class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """

        M = 10**9 + 7

        dic = {}




        def getnext(index, pre):
            if index==len(nums):
                return 1

            if (index, pre) in dic:  
                return dic[(index,pre)]

            output = 0

            for i in range(nums[index]+1):
                j = nums[index] - i
                if pre > i:  
                    continue 

                if index > 0 and j > nums[index-1] - pre:
                    continue 

                output += getnext(index+1, i)
                output %= M

            dic[(index,pre)] = output
            return output 


        ans = getnext(0,-1)

        return ans
