#define ll long long
const ll mod = 1e9 + 7 ;

class Solution {
public:
    ll helper(vector<int>& nums, int idx, int prev, vector<vector<ll>>& dp) {
        if (idx == nums.size()) return 1;

        if (prev != -1 && dp[idx][prev] != -1) return dp[idx][prev];

        ll temp = 0;
        if (prev == -1) {
            for (int i = 0; i <= nums[idx]; i++) {
                temp = (temp + helper(nums, idx + 1, i, dp)) % mod;
            }
        } else {
            for (int i = prev; i <= nums[idx]; i++) {
                int currA = i, currB = nums[idx] - i;
                int prevA = prev, prevB = nums[idx - 1] - prev;

                if (currA >= prevA && currB <= prevB) {
                    temp = (temp + helper(nums, idx + 1, i, dp)) % mod;
                }
            }
        }

        if (prev != -1) dp[idx][prev] = temp % mod;
        return temp;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<ll>> dp(n + 1, vector<ll>(51, -1));
        ll ans = helper(nums, 0, -1, dp);
        return ans;
    }
};
