class Solution {
    int mod = (int)1e9 + 7;
    public int countOfPairs(int[] nums) {
        int dp[][][] = new int[nums.length][51][51];
        for(int mat[][] : dp){
            for(int arr[] : mat){
                Arrays.fill(arr, -1);
            }
        }
        return solve(dp,0, 0, 50, nums);
    }
    public int solve(int dp[][][], int i, int a1, int a2, int [] arr){
        if(i == arr.length){
            return 1;
        }

        if(dp[i][a1][a2] != -1) return dp[i][a1][a2];

        long cnt = 0;
        for(int j = 0; j<=arr[i]; j++){
            if(arr[i] - j <= a2 && j >= a1){
                cnt = (cnt + solve(dp,i + 1, j, arr[i] - j, arr))%mod;
            }
        }

        return dp[i][a1][a2] = (int)(cnt % mod);

    }
}
